import { createSelector } from 'reselect';

// use an input selector function to get a state and return a slice of it
const selectCart = (state) => state.cart

//take an array of input selectors and return a function that returns the cvalue we want
//out of the selector
export const selectCartItems = createSelector(
    [selectCart],
    (cart) => cart.cartItems
)
